import React, { Fragment, useEffect, useState } from "react";
import { connect, useSelector } from "react-redux";
import { Helmet } from 'react-helmet';
import M from 'materialize-css';

import questions from '../../questions.json';
import { quizRequest } from "../../redux";
import { Link } from 'react-router-dom';
import isEmpty from './is-empty';
import './_quiz.scss';

function Quiz({ quizStatus, quizRequest}){
    
    
    // let questions = [];
    // let currentQuestion = {};
    // let nextQuestion = {};
    // let previousQuestion = {};
    // let answer = '';
    // let numberOfQuestions = 0;
    // let numberOfAnsweredQuestion = 0;
    // let currentQuestionsIndex = 0;
    // let score = 0;
    // let correctAnswer = 0;
    // let WrongAnswer = 0;
    // let time = {};

    // const [myQuestions, setMyquestions] = useState({});
    const [currentQuestion, setCurrentQuestion] = useState({});
    const [nextQuestion, setNextQuestion] = useState({});
    const [previousQuestion, setPreviousQuestion] = useState({});
    const [answer, setAnswer] = useState('');
    const [numberOfQuestions,setNumberOfQuestions] = useState(0);
    const [numberOfAnswered,setNumberOfAnswered] = useState(0);
    const [currentQuestionIndex,setCurrentQuestionIndex] = useState(0);
    const [score,setScore] = useState(0);
    const [correctAnswers, setCorrectAnswers] = useState(0);
    const [wrongAnswers,setWrongAnswers] = useState(0);
    const [time, setTime] = useState({});
    const [index, setIndex] = useState(0);
    // const quizLoad = quizStatus.map((item,index) => (
    //     <div key={index}>
    //     {
    //         (questions.push(item.question),
    //         currentQuestion = questions[currentQuestionsIndex]

    //         )
    //     }
    //     </div>
    // ));
    
    useEffect(()=> {
        quizRequest();
        displayQuestions();
    }, []);

    const displayQuestions = () => {  
        setCurrentQuestion(questions[currentQuestionIndex]);
        setNextQuestion(questions[currentQuestionIndex + 1]);
        setPreviousQuestion(questions[currentQuestionIndex -1]);
        setAnswer(currentQuestion.answer);
        setNumberOfQuestions(questions.length);
        console.log(questions);
    }

    const handleOptionClick = (e) => {
        setAnswer('c');
        if (e.target.innerHTML.toLowerCase() === answer.toLowerCase())  {
            clickCorrect();
        }else {
            clickWrong();
        }
    }
    const clickCorrect = () => {
        M.toast({
            html: '정답입니다!',
            classes: 'toast-valid',
            displayLength: 1500
        });
        setScore(score+1);
        setCorrectAnswers(correctAnswers+1);
        setCurrentQuestionIndex(currentQuestionIndex+1);
        setNumberOfAnswered(numberOfAnswered+1);
    }
    const clickWrong = () => {
        navigator.vibrate(1000);
        M.toast({
            html: '틀렸습니다!',
            classes: 'toast-invalid',
            displayLength: 1500
        });
        setWrongAnswers(wrongAnswers+1);
        setCurrentQuestionIndex(currentQuestionIndex+1);
        setNumberOfAnswered(numberOfAnswered);
    }

    return (
        <Fragment>
            <Helmet><title>Quiz Page</title></Helmet>
            <div className = "questions">
                <h2>오늘의 퀴즈</h2>
                <div className = "lifeline-container">
                    <p>
                        <span className = "mdi mdi-clock-outline mdi-24px"></span>
                        <span className = "lifeline">5</span>
                    </p>
                    <p>
                        <span className = "mdi mdi-clock-outline mdi-24px"></span>
                        <span className = "lifeline">5</span>
                    </p>
                </div>
                <div>
                    <p>
                        <span>1 of 10</span>
                    </p>
                </div>
                <h5>{currentQuestion.question}</h5>
                <div className="options-container">
                    <p onClick={handleOptionClick} className="option">a</p>
                    <p onClick={handleOptionClick} className="option">b</p>
                </div>
                <div className="options-container">
                    <p onClick={handleOptionClick} className="option">c</p>
                    <p onClick={handleOptionClick} className="option">d</p>
                </div>
                <div className="button-container">
                    <button>이전</button>
                    <button>다음</button>
                    <button>나가기</button>
                </div>
            </div>
        </Fragment>
        
    );
}

const mapStateToProps = (state) => {
    return {
        quizStatus: state.quiz.status.quizStatus,
    };
};
const mapDispatchToProps = (dispatch) => {
    return {
        quizRequest: () => {
            return dispatch(quizRequest());
        }
    };
};


export default connect(mapStateToProps, mapDispatchToProps)(Quiz);
