          <Grid item xs={3}>
            <ThemeWord></ThemeWord>
          </Grid>
          <Grid item xs={2}>
            <Side></Side>
          </Grid>
        </Grid>
      </div>
    </div>
  );
}
const mapStateToProps = (state) => {
  return {
    // userID: state.authentication.status.currentUser,
    dailyWordsList: state.dailyWords.status.dailyWordsList,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
