import React, { useState } from 'react';
import { Button, Divider, TextField } from "@material-ui/core";
import "./MainWordMeaning.css";
import Grid from '@material-ui/core/Grid';

export default function MainWordMeaning({
  handleOneWord,
  wordStatus
}) {
  const [checked, setChecked] = useState(false);
  const [search, setSearch] = useState(null);

  const handleChange = (e) => {
    setSearch(e.target.value);
  }
  const searchOneWord = () => {
    handleOneWord(search);
    setChecked(true);
  }

  const searchPage = (
    (
      <div className="inputs">
        <div> 문장 / 단어</div>
        <TextField
          className="search"
          id="filled-basic"
          label="원하는 단어나 문장을 입력해 주세요."
          onChange={handleChange}
        />
        <button
          className="search_button"
          onClick={searchOneWord}
        >
          검색
        </button>
      </div>
    )
  )
  const searchSuccess = (
    <div>
      <Button href={"/Word/" + search} color="inherit">
        더 알아보기
      </Button>
    </div>
  )
  console.log(wordStatus);
  return (

    <div className="header">
      <span className="title"> 이건 무슨 뜻이지? </span>
      <Grid container spacing={3}>
        <Grid item xs={6}>
          {!checked ? searchPage : (<div></div>)}
        </Grid>
        <Grid item xs={6}>
          <div className="input">
            test
          </div>
        </Grid>
      </Grid>

    </div>

  )
}
