import React, { useState, useCallback } from "react";
import { Button, TextField } from "@material-ui/core";
import "./VoiceRecognition.css";
import Grid from "@material-ui/core/Grid";
import { Link } from "react-router-dom";

import ReactAudioPlayer from "react-audio-player";

export default function VoiceRecognition({ onClickVoiceRecognition }) {
  const [stream, setStream] = useState();
  const [media, setMedia] = useState();
  const [onRec, setOnRec] = useState(true);
  const [isCheckRec, setIsCheckRec] = useState(true);
  const [source, setSource] = useState();
  const [analyser, setAnalyser] = useState();
  const [audioUrl, setAudioUrl] = useState();

  let soundFile;

  const onRecAudio = () => {
    // 음원정보를 담은 노드를 생성하거나 음원을 실행또는 디코딩 시키는 일을 한다
    const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
    // 자바스크립트를 통해 음원의 진행상태에 직접접근에 사용된다.
    const analyser = audioCtx.createScriptProcessor(0, 1, 1);
    setAnalyser(analyser);

    function makeSound(stream) {
      // 내 컴퓨터의 마이크나 다른 소스를 통해 발생한 오디오 스트림의 정보를 보여준다.
      const source = audioCtx.createMediaStreamSource(stream);
      setSource(source);
      source.connect(analyser);
      analyser.connect(audioCtx.destination);
    }
    // 마이크 사용 권한 획득
    navigator.mediaDevices.getUserMedia({ audio: true }).then((stream) => {
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorder.start();
      setStream(stream);
      setMedia(mediaRecorder);
      makeSound(stream);

      analyser.onaudioprocess = function (e) {
        // 3분(180초) 지나면 자동으로 음성 저장 및 녹음 중지
        if (e.playbackTime > 180) {
          stream.getAudioTracks().forEach(function (track) {
            track.stop();
          });
          mediaRecorder.stop();
          // 메서드가 호출 된 노드 연결 해제
          analyser.disconnect();
          audioCtx.createMediaStreamSource(stream).disconnect();

          mediaRecorder.ondataavailable = function (e) {
            setAudioUrl(e.data);
            setOnRec(true);
          };
        } else {
          setOnRec(false);
        }
      };
    });
  };

  // 사용자가 음성 녹음을 중지했을 때
  const offRecAudio = () => {
    // dataavailable 이벤트로 Blob 데이터에 대한 응답을 받을 수 있음
    media.ondataavailable = function (e) {
      setAudioUrl(e.data);
      setOnRec(true);
    };

    // 모든 트랙에서 stop()을 호출해 오디오 스트림을 정지
    stream.getAudioTracks().forEach(function (track) {
      track.stop();
    });

    // 미디어 캡처 중지
    media.stop();
    // 메서드가 호출 된 노드 연결 해제
    analyser.disconnect();
    source.disconnect();
  };

  const onSubmitAudioFile = useCallback(() => {
    if (audioUrl) {
      console.log(URL.createObjectURL(audioUrl)); // 출력된 링크에서 녹음된 오디오 확인 가능
    }
    // File 생성자를 사용해 파일로 변환
    const sound = new File([audioUrl], "soundBlob", {
      lastModified: new Date().getTime(),
      type: "audio",
    });
    soundFile = sound;
    //console.log(sound); // File 정보 출력
    console.log(soundFile); // File 정보 출력
    setIsCheckRec(false);
  }, [audioUrl]);

  const onSearchAudioFile = (e) => {
    let body = {
      voiceFile: soundFile,
    };
  };

  const onRecAudioButton = (
    <Button color="primary" variant="contained" onClick={onRecAudio}>
      녹음시작
    </Button>
  );
  const offRecAudioButton = (
    <Button color="primary" variant="contained" onClick={offRecAudio}>
      녹음종료
    </Button>
  );

  const checkRec = (
    <Button color="primary" variant="contained" onClick={onSubmitAudioFile}>
      결과확인
    </Button>
  );
  const checkedRec = (
    <div>
      <Grid item xs={12}>
        <Button color="primary" variant="contained" onClick={onSubmitAudioFile}>
          결과확인
        </Button>
      </Grid>
      <Grid item xs={12}>
        {audioUrl ? (
          <ReactAudioPlayer src={URL.createObjectURL(audioUrl)} controls />
        ) : undefined}
      </Grid>
    </div>
  );

  return (
    <div className="header">
      <h3 className="title"> 음성으로 검색하기 </h3>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          {onRec ? onRecAudioButton : offRecAudioButton}
        </Grid>
        <Grid item xs={12}>
          {isCheckRec ? checkRec : checkedRec}
        </Grid>
        <Grid item xs={12}>
          <Button
            color="primary"
            variant="contained"
            onClick={onSearchAudioFile}
          >
            검색하기
          </Button>
        </Grid>
      </Grid>
    </div>
  );
}
