import React, { useState } from "react";
import { useSpeechRecognition } from "react-speech-kit";

export default function VoiceRecognition2() {
  const [isCheckRec, setIsCheckRec] = useState(true);
  const [value, setValue] = useState("");
  const { listen, listening, stop } = useSpeechRecognition({
    onResult: (result) => {
      // 음성인식 결과가 value 상태값으로 할당됩니다.
      setValue(result);
    },
  });

  const onRecAudioButton = (
    <Button
      color="primary"
      variant="contained"
      onClick={() => listen({ interimResults: false })}
    >
      녹음시작
    </Button>
  );
  const offRecAudioButton = (
    <Button
      color="primary"
      variant="contained"
      onClick={() => {
        stop;
      }}
    >
      녹음종료
    </Button>
  );

  const checkRec = (
    <Button color="primary" variant="contained" onClick={onSubmitAudioFile}>
      결과확인
    </Button>
  );

  return (
    <div className="header">
      <h3 className="title"> 음성으로 검색하기 </h3>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          {onRec ? onRecAudioButton : offRecAudioButton}
        </Grid>
        <Grid item xs={12}>
          {isCheckRec ? checkRec : checkedRec}
        </Grid>
      </Grid>
    </div>
  );
}
