import React, { useEffect, useState } from "react";
import {} from "../redux";

import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";

const useStyles = makeStyles((theme) => ({
  root: {
    paddingTop: theme.spacing(3),
    paddingLeft: theme.spacing(5),
    paddingRight: theme.spacing(5),
    paddingBottom: theme.spacing(5),

    // padding: theme.spacing(10),
    // width: '100%',
    // maxWidth: '30ch',
    height: "42ch",
    backgroundColor: theme.palette.background.paper,
    border: "1px solid #D9D9D9",
    borderRadius: "20px",
  },
  test: {
    // height: '50%',
    float: "right",
    // border: '1px solid #D9D9D9',
    // position: 'abolute',
    // left: '50%',
    // top: '50%',
    // transform: 'translate(-50%, -50%)',

    // display: 'flex',
    // justifyContent: 'center',
    // alignItems: 'center',
  },
  box: {
    border: "1px solid #D9D9D9",
  },
  bottom: {
    display: "inline",
    borderBottom: "1px solid #D9D9D9",
  },
}));

export default function SentenceParaphrase({ paraphraseCheckRequest }) {
  useEffect(() => {
    // 렌더링
  }, []);
  const classes = useStyles();

  const [userSentence, setUserSentence] = useState("");

  const handleChange = ({ target }) => {
    setUserSentence(target.value);
  };

  let sentence1 =
    "성탄 전야 미사를 집전하며 프란치스코 교황이 전한 메시지는 '어린이를 향한 관심'입니다.";
  // 사용자에게 보여지는 부분
  return (
    <div className={classes.root}>
      <Grid container spacing={1}>
        <Grid item xs={5}>
          <h3>문장 스터디</h3>
        </Grid>
        <Grid item xs={2}></Grid>
        <Grid item xs={5}>
          <h3 />
          <button className={classes.test}>더 알아보기</button>
        </Grid>
        <h5>{sentence1}</h5>
        <TextField
          id="standard-textarea"
          multiline
          label="같은 뜻의 문장을 입력하세요."
          name="sentence"
          autoFocus
          fullWidth
          value={userSentence}
          onChange={handleChange}
        />
      </Grid>
    </div>
  );
}
