import React, { Fragment, useEffect, useState } from "react";
import { connect, useSelector } from "react-redux";
import { Helmet } from 'react-helmet';
import M from 'materialize-css';

import questions from '../../questions.json';
import { quizRequest } from "../../redux";
import { Link, useHistory } from 'react-router-dom';
import isEmpty from './is-empty';
import './_quiz.scss';

function Quiz({ quizStatus, quizRequest}){
    
    
    let history = useHistory();

    const [myQuestions, setMyQuestions] = useState({});
    const [currentQuestion, setCurrentQuestion] = useState({});
    const [nextQuestion, setNextQuestion] = useState({});
    const [previousQuestion, setPreviousQuestion] = useState({});
    var currentAnswer = '';
    const [numberOfQuestions,setNumberOfQuestions] = useState(0);
    const [numberOfAnswered,setNumberOfAnswered] = useState(0);
    const [currentQuestionIndex,setCurrentQuestionIndex] = useState(0);
    const [score,setScore] = useState(0);
    const [correctAnswers, setCorrectAnswers] = useState(0);
    const [wrongAnswers,setWrongAnswers] = useState(0);
    const [time, setTime] = useState({});
    const [index, setIndex] = useState(0);
    const [interval, setInterval] = useState(null);

    useEffect(()=> {
        quizRequest();
        displayQuestions();
        startTimer();
    }, []);

    const displayQuestions = () => { 
        setMyQuestions(questions);
        console.log(currentQuestionIndex);
        setCurrentQuestion(questions[currentQuestionIndex]);
        setNextQuestion(questions[currentQuestionIndex + 1]);
        setPreviousQuestion(questions[currentQuestionIndex -1]);
        setNumberOfQuestions(questions.length);
        currentAnswer = currentQuestion.answer;
    }
    const endGame = () => {

    }
    const handleOptionClick = (e) => {
        currentAnswer = currentQuestion.answer;
        console.log(currentAnswer);
        if (e.target.innerHTML.toString().toLowerCase() === currentAnswer.toLowerCase())  {
            clickCorrect();
        }else {
            clickWrong();
        }
    }
    const clickCorrect = () => {
        M.toast({
            html: '정답입니다!',
            classes: 'toast-valid',
            displayLength: 1500
        });
        setScore(score+1);
        setCorrectAnswers(correctAnswers+1);
        setCurrentQuestionIndex(currentQuestionIndex+1);
        setNumberOfAnswered(numberOfAnswered+1);
        if (nextQuestion === undefined) {
            endGame();
        } else {
            displayQuestions();
        }
    }
    const clickWrong = () => {
        navigator.vibrate(1000);
        M.toast({
            html: '틀렸습니다!',
            classes: 'toast-invalid',
            displayLength: 1500
        });
        setWrongAnswers(wrongAnswers+1);
        setCurrentQuestionIndex(currentQuestionIndex+1);
        setNumberOfAnswered(numberOfAnswered);
        if (nextQuestion === undefined) {
            endGame();
        } else {
            displayQuestions();
        }
    }
    const handleNextButtonClick = () => {
        if (nextQuestion !== undefined) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
            displayQuestions();
        }
    };
    const handlePreviousButtonClick = () => {
        if (previousQuestion == undefined) {
            M.toast({
                html: '이전 문제가 없습니다.',
                classes: 'toast-invalid',
                displayLength: 1500
            });
            displayQuestions();
        }
        else if (nextQuestion !== undefined) {
            setCurrentQuestionIndex(currentQuestionIndex - 1);
            displayQuestions();
        }
    };
    const handleQuitButtonClick = () => {
        if (window.confirm('나가시겠습니까?')) {
            history.push('/Home');
        }
    };
    const handleButtonClick = (e) => {
        switch (e.target.id) {
            case 'next-button':
                handleNextButtonClick();
                break;
            case 'previous-button':
                handlePreviousButtonClick();
                break;
            case 'quit-button':
                handleQuitButtonClick();
                break;
            default:
                break;
        }
        
    };
    const startTimer = () => {
        const countDownTime = Date.now + 30000;
        setInterval(() => {
            const now = new Date();
            const distance = countDownTime - now;

            const minutes = Math.floor(distance % (1000 * 60 * 60) / (1000 * 60));
            const seconds = Math.floor(distance % (1000 * 60) / (1000));

            if (distance < 0) {
                clearInterval(interval);
                setTime({
                        minutes: 0,
                        seconds: 0
                }, () => {
                    alert('퀴즈가 끝났습니다!');
                    history.push('/Home');
                });
            } else {
                setTime({
                        minutes,
                        seconds,
                        distance
                    }
                )
            }
        }, 1000);
    }
    return (
        <Fragment>
            <Helmet><title>Quiz Page</title></Helmet>
            <div className = "questions">
                <h2>오늘의 퀴즈</h2>
                <div className = "lifeline-container">
                    {/* <p>
                        <span className = "mdi mdi-clock-outline mdi-24px"></span>
                        <span className = "lifeline">5</span>
                    </p>
                    <p>
                        <span className = "mdi mdi-clock-outline mdi-24px"></span>
                        <span className = "lifeline">5</span>
                    </p> */}
                </div>
                <div className = "timer-container">
                    <p>
                        <span className= "left" style={{float: 'left'}}>
                            {currentQuestionIndex+1} of {numberOfQuestions}</span>
                        <span className= "right">
                                {time.minutes}:{time.seconds}
                            <span  className="mdi mdi-clock-outline mdi-24px"></span></span>
                    </p>
                </div>
                <h5>{currentQuestion.question}</h5>
                {console.log(currentQuestion)}
                {console.log(currentQuestionIndex)}
                <div className="options-container">
                    <p onClick={handleOptionClick} className="option">{currentQuestion.optionA}</p>
                    <p onClick={handleOptionClick} className="option">{currentQuestion.optionB}</p>
                </div>
                <div className="options-container">
                    <p onClick={handleOptionClick} className="option">{currentQuestion.optionC}</p>
                    <p onClick={handleOptionClick} className="option">{currentQuestion.optionD}</p>
                </div>
                <div className="button-container">
                    <button id="previous-button" onClick={handleButtonClick}>이전</button>
                    <button id="next-button" onClick={handleButtonClick}>다음</button>
                    <button id="quit-button" onClick={handleButtonClick}>나가기</button>
                </div>
            </div>
        </Fragment>
        
    );
}

const mapStateToProps = (state) => {
    return {
        quizStatus: state.quiz.status.quizStatus,
    };
};
const mapDispatchToProps = (dispatch) => {
    return {
        quizRequest: () => {
            return dispatch(quizRequest());
        }
    };
};


export default connect(mapStateToProps, mapDispatchToProps)(Quiz);
