import axios from "axios";
import {
  KAKAO_AUTHORIZE,
  KAKAO_AUTHORIZE_SUCCESS,
  KAKAO_AUTHORIZE_FAILURE,
  KAKAO_LOGOUT,
} from "./types";

/* KAKAO AUTH */
export function kakaoAuthRequest(code) {
  return (dispatch) => {
    // Inform Login API is starting
    dispatch(kakaoAuth());

    // API REQUEST
    return axios
      .get("http://localhost:8080/kakaoAuth/", {
        params: {
          code: code,
        },
      })
      .then((response) => {
        // SUCCEED
        dispatch(kakaoAuthSuccess(response.data));
        // 성공해서 토큰 받으면 저장해주기
        console.log(response.data);
      })
      .catch((error) => {
        // FAILED
        dispatch(kakaoAuthFailure());
      });
  };
}

export function kakaoAuth() {
  return {
    type: KAKAO_AUTHORIZE,
  };
}

export function kakaoAuthSuccess(token) {
  return {
    type: KAKAO_AUTHORIZE_SUCCESS,
    token,
  };
}

export function kakaoAuthFailure() {
  return {
    type: KAKAO_AUTHORIZE_FAILURE,
  };
}

/* Check KAKAO User */
export function checkKakaoUserRequest() {
  return (dispatch) => {
    // inform Get Status API is starting
    dispatch(checkUser());

    return axios
      .get("http://localhost:8080/checkKakaoUser")
      .then((response) => {
        dispatch(checkUserSuccess(response.data)); //HTTP 틍신을 통해 userId을 빋이옴
      })
      .catch((error) => {
        dispatch(checkUserFailure());
      });
  };
}

export function checkUser() {
  return {
    type: USER_VERIFICATION,
  };
}

export function checkUserSuccess(userId) {
  return {
    type: USER_VERIFICATION_SUCCESS,
    userId,
  };
}

export function checkUserFailure() {
  return {
    type: USER_VERIFICATION_FAILURE,
  };
}

/* KAKAO Logout */
/* Logout */
export function kakaoLogoutRequest() {
  return (dispatch) => {
    return axios.post("http://localhost:8080/kakaoLogout").then((response) => {
      dispatch(kakaoLogout());
    });
  };
}

export function kakaoLogout() {
  return {
    type: KAKAO_LOGOUT,
  };
}
